package com.coaware.social.common.utils;import java.security.MessageDigest;import java.security.NoSuchAlgorithmException;public class MD5Util {    private static final String HEX_CHARS = "0123456789ABCDEF";    /**     * Returns a MessageDigest for the given algorithm.     *     * @return An MD5 digest instance.     */    static MessageDigest getDigest() {        try {            return MessageDigest.getInstance("MD5");        } catch (NoSuchAlgorithmException e) {            throw new RuntimeException(e);        }    }    /**     * Calculates the MD5 digest and returns the value as a 16 element byte[].     *     * @param data Data to digest     * @return MD5 digest     */    public static byte[] md5(byte[] data) {        return getDigest().digest(data);    }    /**     * Calculates the MD5 digest and returns the value as a 16 element byte[].     *     * @param data Data to digest     * @return MD5 digest     */    public static byte[] md5(String data) {        return md5(data.getBytes());    }    /**     * Calculates the MD5 digest and returns the value as a 32 character hex string.     *     * @param data Data to digest     * @return MD5 digest as a hex string     */    public static String md5Hex(byte[] data) {        return toHexString(md5(data));    }    /**     * Calculates the MD5 digest and returns the value as a 32 character hex string.     *     * @param data Data to digest     * @return MD5 digest as a hex string     */    public static String md5Hex(String data) {        return toHexString(md5(data));    }    public static String toHexString(byte[] b) {        StringBuffer sb = new StringBuffer();        for (int i = 0; i < b.length; i++) {            sb.append(MD5Util.HEX_CHARS.charAt(b[i] >>> 4 & 0x0F));            sb.append(MD5Util.HEX_CHARS.charAt(b[i] & 0x0F));        }        return sb.toString();    }        public static void main(String[] args){    	System.out.println(md5Hex("123456"+"nm-mobile-pwd_2016"));    }    }